import axios from 'axios';
import Pusher from 'pusher-js'
import Plotly from 'plotly.js'
import {  mapGetters } from 'vuex';

export default {
    name: "FieldChart",
    data() {
        return {
            valeur: null,
            date: null,
            chart: null,
            id: '',
            file: null,
            idfield: null,

        }
    },
    computed: {
        ...mapGetters(['getApiUrl'])
    },
    props: ['nameField', 'idField'],
    created() {
        this.id = this.nameField + '-' + this.idField;
        //   Pusher.logToConsole = true;

        var key = this.$store.state.canal.cleLecture,
            keyWrite = this.$store.state.canal.cleEcriture,
            token = this.$store.state.token,
            nameF = this.nameField,
            idChart = this.id;

        var pusher = new Pusher('84bee67aad46ed497369', {
            cluster: 'eu',
            forceTLS: true
        });

        var channel = pusher.subscribe('my-channel'),
            apiUrl  =  this.getApiUrl;
        channel.bind('my-event', function (data) {
            if (data['data'] === keyWrite) {
                axios.get( apiUrl +  '/canal-service/read?key=' + key + '&field=' + nameF, {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': 'Bearer ' + token
                    }
                })
                    .then(response => {
                        let results = response.data;
                        let dateresult = results.map(a => a.date);
                        let valueresult = results.map(a => a.valeur);
                        this.valeur = valueresult;
                        this.date = dateresult;

                        let x = [];

                        for (let i = 0; i < dateresult.length; i++) {
                            x.push(i);
                        }

                        var layout = {
                            title: nameF,
                            xaxis: {
                                title: 'Date',
                                showticklabels: false,
                                tickangle: 'auto',
                                tickvals: x,
                                ticktext: dateresult

                            },
                            yaxis: {
                                title: 'Values',
                                showticklabels: true,
                            }
                        };
                        Plotly.newPlot(idChart, [{y: valueresult}], layout);

                    })
                    .catch(error => {
                    })
            }
        });

    },
    mounted() {
        $('.tooltipped').tooltip();
        this.getData();
        $('.fixed-action-btn').floatingActionButton({
            hoverEnabled: false,
            direction: 'left',
        });
    },
    methods: {
        getData() {
            var key = this.$store.state.canal.cleLecture,
                token = this.$store.state.token,
                nameF = this.nameField,
                apiUrl  =  this.getApiUrl,
                idChart = this.id;
            axios.get( apiUrl +  '/canal-service/read?key=' + key + '&field=' + nameF, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + token
                }
            })
                .then(response => {
                    let results = response.data;
                    let dateresult = results.map(a => a.date);
                    let valueresult = results.map(a => a.valeur);
                    this.valeur = valueresult;
                    this.date = dateresult;

                    let x = [];

                    for (let i = 0; i < dateresult.length; i++) {
                        x.push(i);
                    }

                    var layout = {
                        //title: nameF,
                        xaxis: {
                            title: 'Date',
                            showticklabels: false,
                            tickangle: 'auto',
                            tickvals: x,
                            ticktext: dateresult

                        },
                        yaxis: {
                            title: 'Values',
                            showticklabels: true,
                        }
                    };
                    Plotly.newPlot(idChart, [{y: valueresult}], layout);
                })
                .catch(error => {
                    console.log(error);
                })
        },


        // import

        processFile(event) {
            this.file = event.target.files[0];
            this.importCSV();
        },
        importCSV: function () {

            if (this.file.type == "application/vnd.ms-excel") {

                let formData = new FormData();
                let apiUrl  =  this.getApiUrl;

                formData.append('file', this.file);

                axios.post( apiUrl +  '/canal-service/import-data/' + this.idField, formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                        'Authorization': 'Bearer ' + this.$store.state.token,
                    }

                }).then(function () {
                    //console.log('SUCCESS!!');
                }).catch(function () {
                    //console.log('FAILURE!!');
                });
            }

        },
    }
}
